import React, {useState} from 'react';
import {useDispatch, useSelector} from "react-redux";
import UserSalaryRow from './UserSalaryRow';
import { Button } from 'react-bootstrap'

const UserSalaryTable = (props) => {
  const salaryuser = useSelector(state => state.salaryuser.salaryuser);
  const dispatch = useDispatch();

  const SalaryRefresh = () =>{
    const Salaryrefresh = {
        salary_id: props.salary.salary_id,
        cost_ph: props.salary.cost_ph,
        total_hours: props.salary.total_hours,
        salary: props.salary.salary, 
        month_s: props.salary.month_s,
        users_id: props.salary.users_id,

    }
    dispatch({type: 'SALARYUSEREDIT', payload: Salaryrefresh})
}
    return (
      <div>

      <table class="table table-dark">
<thead>
<tr>
<th scope="col">ID</th>
<th scope="col">Cost</th>
<th scope="col">Total Hours</th>
<th scope="col">Salary</th>
<th scope="col">Month</th>
<th scope="col"><Button variant="btn btn-warning" onClick = {SalaryRefresh}>Refresh</Button></th>
</tr>
</thead>
<tbody>
    {
      salaryuser.map((salary) => <UserSalaryRow salary = {salary} />)
    }
</tbody>
</table>
  </div>
        
    )
}

export default UserSalaryTable;
