const initialState = {
    isLoggedIn: false,
    user: [],
}

// export const LOGIN = "LOGIN";

export const UserReducer = (state = initialState, action) => {
    switch (action.type){
        case LOGIN:
            return {...state, user: action.payload, isLoggedIn: true}
        default:
            return state
    }
}
