const initialState = {
    isLoggedIn: false,
    user: [],
}

export const ADD_USER = "ADD_USER";

export const UserReducer = (state = initialState, action) => {
    switch (action.type){
        case ADD_USER:
            return {...state, user: action.payload, isLoggedIn: true}
        // case REMOVE_USER:
        //     return {...state, users: state.users.filter(user => user.id !== action.payload)}
        default:
            return state
    }
}
