import React, {useState} from 'react';
import UserEditing from '../UserEditind';
import {Redirect} from 'react-router-dom';
import {useDispatch} from "react-redux";



import Company_Admin from '../Components/Company_Admin';
import {  Nav, Navbar, Container, Form, Button } from 'react-bootstrap'
import NavbarToggle from 'react-bootstrap/esm/NavbarToggle'
import NavbarCollapse from 'react-bootstrap/esm/NavbarCollapse'
import { BrowserRouter as Router, Switch, Route, Link } from 'react-router-dom'
import Home from '../Pages/Home'
import About from '../Pages/About'
import Contacts from '../Pages/Contacts'
import Admin from '../Components/Admin';
import User from '../Components/User';
import UserTable from '../Components/Admin Components/Usertable';
import CompaniesTable from '../Components/Admin Components/Companiestable';
import Worktimetable from '../Components/Admin Components/Worktimetable';
import UserEditind from '../Components/UserEditind';


const UserRow = (props) =>{
    const dispatch = useDispatch();
    
    const [redirect, setRedirect] = useState(false);
    const Edit = () =>{
        const User = {
            id: props.users.id,
            first_name: props.users.first_name,
            last_name: props.users.last_name,
            email: props.users.email, 
            company_id: props.users.company_id
        }
        dispatch({type: 'USEREDIT', payload: User})
        setRedirect(true);
    }
    if(redirect){
        return <Redirect to='/useredit'/>
    }
    return(
        <tr>
        <th scope="row">{props.users.id}</th>
        <td>{props.users.first_name}</td>
        <td>{props.users.last_name}</td>
        <td>{props.users.email}</td>
        <td>{props.users.company_id}</td>
        <td><Button variant="outline-secondary" onClick = {Edit}>Edit</Button></td>
        <td>Remove</td> 
        </tr>
        
    )
}

export default UserRow;
